import{_ as a,o as e,c as l,Q as r}from"./chunks/framework.67847481.js";const b=JSON.parse('{"title":"市场参与者","description":"","frontmatter":{"title":"市场参与者","desc":"简要介绍市场参与者","author":"tobetraders","createTime":"2023-09-21T00:00:00.000Z"},"headers":[],"relativePath":"knowledge/7_market_player.md","filePath":"knowledge/7_market_player.md"}'),t={name:"knowledge/7_market_player.md"},i=r('<h2 id="概述" tabindex="-1">概述 <a class="header-anchor" href="#概述" aria-label="Permalink to &quot;概述&quot;">​</a></h2><p>市场参与者是在金融学中指金融市场的参与主体。分两类：一类是真正的买者和卖者，即交易主体；一类是金融市场媒体，又称金融中介。金融参与者还可分成家庭、企业和政府，都是金融市场上的主要的资金供给者或资金需求者，因而是金融市场的交易主体。家庭部门有时又称个人部门，是金融市场上重要的资金供给者，或者说是金融工具的主要认购或投资者。企业因从事商品生产而和金融市场紧紧联系在一起，公司资本来源的多样化和投资主体的多元化，意味着企业成为金融市场上证券发行的主体，成为决定初级市场规模的主要因素，金融中介本身也是企业。政府除了是金融市场上最大的资金需求者和交易的主体之外，还是重要的监管者和调节者，因而在金融市场上的身份是双重的。</p><h2 id="交易者" tabindex="-1">交易者 <a class="header-anchor" href="#交易者" aria-label="Permalink to &quot;交易者&quot;">​</a></h2><h2 id="做市商" tabindex="-1">做市商 <a class="header-anchor" href="#做市商" aria-label="Permalink to &quot;做市商&quot;">​</a></h2><h2 id="发行方" tabindex="-1">发行方 <a class="header-anchor" href="#发行方" aria-label="Permalink to &quot;发行方&quot;">​</a></h2><h2 id="资金方" tabindex="-1">资金方 <a class="header-anchor" href="#资金方" aria-label="Permalink to &quot;资金方&quot;">​</a></h2><h2 id="托管方" tabindex="-1">托管方 <a class="header-anchor" href="#托管方" aria-label="Permalink to &quot;托管方&quot;">​</a></h2><h2 id="中介机构" tabindex="-1">中介机构 <a class="header-anchor" href="#中介机构" aria-label="Permalink to &quot;中介机构&quot;">​</a></h2><ul><li>交易所</li><li>券商</li><li>投行</li><li>期货公司</li></ul><h2 id="自律性组织" tabindex="-1">自律性组织 <a class="header-anchor" href="#自律性组织" aria-label="Permalink to &quot;自律性组织&quot;">​</a></h2><ul><li>行业协会</li><li>中小投资者教育</li></ul><h2 id="监管机构" tabindex="-1">监管机构 <a class="header-anchor" href="#监管机构" aria-label="Permalink to &quot;监管机构&quot;">​</a></h2><ul><li>中国证监会</li><li>美国SEC</li></ul><h2 id="工具开发商" tabindex="-1">工具开发商 <a class="header-anchor" href="#工具开发商" aria-label="Permalink to &quot;工具开发商&quot;">​</a></h2><ul><li>行情提供方</li><li>指数编撰机构</li><li>交易软件开发商</li></ul><h2 id="市场服务" tabindex="-1">市场服务 <a class="header-anchor" href="#市场服务" aria-label="Permalink to &quot;市场服务&quot;">​</a></h2><ul><li>培训机构</li><li>信息提供方</li></ul>',17),o=[i];function h(n,d,s,c,u,_){return e(),l("div",null,o)}const q=a(t,[["render",h]]);export{b as __pageData,q as default};
